DROP TABLE IF EXISTS `news_article`;
CREATE TABLE `news_article` (
  `article_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `column_id` int(10) DEFAULT NULL COMMENT '栏目id/文章类别',
  `article_title` varchar(60) NOT NULL COMMENT '文章标题',
  `article_author` varchar(20) NOT NULL COMMENT '文章作者',
  `article_introduction` varchar(400) NOT NULL COMMENT '文章导语',
  `article_addtime` int(10) NOT NULL COMMENT '文章发布时间',
  `article_picture` varchar(100) NOT NULL COMMENT '文章相关图片',
  `article_content` text NOT NULL COMMENT '文章内容',
  `article_views` int(10) NOT NULL DEFAULT '0' COMMENT '文章访问量',
  `article_agree` int(10) NOT NULL DEFAULT '0' COMMENT '文章赞数/赞同',
  `article_disagree` int(10) NOT NULL DEFAULT '0' COMMENT '文章猜数/不赞同',
  `article_comments` int(10) NOT NULL DEFAULT '0' COMMENT '文章评论条数',
  `article_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '文章状态(0发布,1等待审核)',
  `is_delete` tinyint(1) NOT NULL DEFAULT '0' COMMENT '逻辑删除',
  PRIMARY KEY (`article_id`),
  KEY `fk_arttocol` (`column_id`)
) ENGINE=MyISAM AUTO_INCREMENT=519 DEFAULT CHARSET=utf8;

